package ex2;

public class Q5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i=100;
		int soma=0;
		
		System.out.println("a soma dos números inteiros do intervalo de 100 até 200:");
		while(i<=200) {
			soma = soma + i;
			i++;
			System.out.println(soma);
		}
	}

}
